mapper.insert:
insert
  into c_member
  	 (
  	   email
  	 , user_pwd
  	 , user_name
  	 , gender
  	 , brithday
  	 , phone
  	 , address
  	 -- ,invite_code -- invite_code 삭제함 - by 동규 (2024.10.15)
  	 )
values
     (
       #{email}
     , #{userPwd}
     , #{userName}
     , #{gender}
     , #{brithday}
     , #{phone}
     , #{address}   
     -- , #{inviteCode} -- invite_code 삭제함 - by 동규 (2024.10.15)
     )

mapper.select:
select email
  from c_member
 where user_name = #{userName}
   and phone = #{phone}
   and status != 'N'

select email
  from c_member
 where user_name = #{userName}
   and email = #{email}
   and status != 'N'

mapper.update:
update c_member
	set user_pwd = #{userPwd}
  where email = #{email}
	and status != 'N'

update c_member
   set user_name = #{userName}
     , nickName = #{nickName}
     , gender = #{gender}
     , phone = #{phone}
     , address = #{address}
     , brithday = #{brithday}
     , modify_date = sysdate
 where email = #{email}

mapper.select:
select count(invite_code) as "count"
 from c_member
where invite_code = #{inviteCode}

mapper.update:
update c_member
   set invite_code = #{inviteCode}
     , modify_date = sysdate
 where email = #{email}

update c_member
   set couple_code = #{coupleCode}
     , partner_email = #{partnerEmail}
     , status = 'Y'
 where email = #{email}

update c_couple
   set status = 'N'
 where couple_code = #{coupleCode}

update c_couple
   set status = 'Y'
 where couple_code = #{coupleCode}
